const SONG_REQUESTS_TABLE = 'song_requests';
const SONG_REQUESTS_PUBLIC_COLUMNS = ['id', 'spotify_id', 'spotify_info', 'spotify_info_error', 'youtube_matches', 'youtube_matches_error', 'key', 'download_error', 'status', 'song_id', 'is_processing', 'completed', 'created_at'].map(c => `${SONG_REQUESTS_TABLE}.${c}`).join(', ');

