    utils.updateWithID(db, songRequestID, allowedFields, data, SONG_REQUESTS_PUBLIC_COLUMNS, SONG_REQUESTS_TABLE)
      .then(segment => finish(null, segment))
      .catch(err => finish(err))
  })
}

module.exports = {
  //
  // Constants
  //
