    db.getSongRequestWithSpotifyID(spotifyID).then(songRequest => resolve(songRequest)).catch(err => {
      if (err.message !== errors.SONG_REQUEST_DOES_NOT_EXIST) return reject(err);
      // check if song already exists
      Song.findOne({ 'spotifyInfo.id': spotifyID }, function (err, song) {
        if (err) {
          console.log(err);
          return rject(err);
        }
        // IF the song already exists, create a completed songRequest with it
        // and resolve with it
        if (song) {
          db.createSongRequest({ spotify_id: spotifyID, status: status.COMPLETED, is_processing: false, song_id: song.id }).then(createdRequest => resolve(_extends({}, createdRequest, { song }))).catch(err => reject(err));
        } else {
          createSongRequest(spotifyID).then(newRequest => resolve(newRequest)).catch(err => reject(err));
