    }

    db.tx(t => {
      let queries = [];

      /**
       * Delete all existing fields matching ID
       */
      queries.push(t.result(`delete from ${table} where ${entityField} = ${entityID}`));

      /**
       * Add these new fields
       */
      if (fieldIDs && fieldIDs.length) {
        queries.push(t.manyOrNone(`insert into ${table} (${entityField}, ${field}) ` + `values(${entityID}, unnest($1)) on conflict do nothing ` + `returning ${columns}`, [fieldIDs]));
      }

      return t.batch(queries);
