  explicit: { type: Boolean },
  isrc: { type: String }
}, {
  toObject: {
    virtuals: true,
    transform: function (doc, ret, options) {
      return {
        id: ret.id,
        key: ret.key,
        isrc: ret.isrc,
        duration: ret.duration,
        __t: ret.__t,
        artist: ret.artist,
        title: ret.title,
        album: ret.album,
        itunesTrackID: ret.itunesTrackID,
