});

songSchema.virtual('eoi').get(function () {
  if (!this._eoi) {
    return 0;
  } else {
    return this._eoi;
  }
}).set(function (value) {
  this._eoi = value;
});

songSchema.virtual('boo').get(function () {
  if (!this._boo) {
    return this.eom;
  } else {
    return this._boo;
  }
}).set(function (value) {
  this._boo = value;
});

songSchema.statics.getSpotifyIDs = function (limit = 1000) {
  return new Promise((resolve, reject) => {
