  if (spotifyInfo["album"] && spotifyInfo["album"]["images"] && spotifyInfo["album"]["images"].length) {
    song.albumArtworkUrl = spotifyInfo["album"]["images"][0]["url"];
  }
  return song;
}

// provide defaults for eom, eoi, boo
songSchema.virtual('eom')
