let amqp = require('amqplib/callback_api');

class EventStream {

  constructor(name, url) {
    this.name = name;
    this.url = url;
    this.channel = null;
    this.queue = null;
    this.handlers = {};
  }

  connect() {
    return new Promise((resolve, reject) => {
      const createChannel = connection => {
        this._createChannel(connection).then(() => createQueue()).catch(error => reject(error));
      };

      const createQueue = () => {
        this._createQueue().then(() => resolve()).catch(error => reject(error));
      };

      amqp.connect(this.url, (error, connection) => {
        if (error) {
          reject(error);
        } else {
          createChannel(connection);
        }
      });
