  setTimeout(() => {
    db.getSongRequestWithSpotifyID(spotifyID).then(songRequest => {
      if (songRequest.status == status.DOWNLOADING_AUDIO) {
        db.updateSongRequestWithID(songRequest.id, { status: status.AUDIO_DOWNLOAD_TIMED_OUT,
