          Promise.all([db.updateSongRequestWithID(songRequest.id, { song_id: foundSong.id }), foundSong.save()]).then(results => resolve(_extends({}, results[0], { song: foundSong }))).catch(err => reject(err));
        } else {
          var song = Song.newFromSpotifyInfo(songRequest.spotify_info);
