    youTubeAudioProcessor.requestAudio({ youTubeIDs, spotifyID, webHookURL, webHookAPIToken }).then(() => {
      scheduleAudioDownloadCheck({ spotifyID });
      resolve();
    }).catch(err => reject(err));
  });
}

